import React, { useState } from 'react'
import Cards from './Cards'
import Score from './Score'

export default function Main() {
  const [state, setState] = useState({
    score: {
      current: 0,
      best: 0
    },
    cards: {
    }
  });

  const handleIncreaseScore = () => {
    setState(
      {
      ...state,
      score: {
        ...state.score,
        current: state.score.current + 1
      }
    })
    if(state.score.current > state.score.best) {
        setState(
          {
          ...state,
          score: {
            ...state.score,
            best: state.score.current
          }
        })
    }
  }

  const handleResetScore = () => {
    setState({
      ...state,
      score: {
        ...state.score,
        current: 0,
      }
    })
  } 

  const {score} = state
  return (
    <div>
      <Score currentScore={score.current} bestScore={score.best}/>
      <Cards handleResetScore={handleResetScore} handleIncreaseScore={handleIncreaseScore}/>
    </div>
  )
}
