    if (card.status === 'not clicked') {
    const stateCopy = [...state]
    shuffleArray(stateCopy);
    stateCopy.map(el => {if(el.id === card.id){
        el.status = 'clicked'
    }})
    console.log(stateCopy)
    setState(stateCopy);
    }
}

